
function cycle(a){var b=$("#full_image");var c=b.children().attr("src");var d=$("ul#images");var e=d.find('[src$="'+c+'"]').parent();if(a=="left"){if(e.is(":first-child")){var f=$("ul#images li:last-child img")}else{var f=e.prev().children()}b.empty();f.clone().appendTo(b)}if(a=="right"){if(e.is(":last-child")){var g=$("ul#images li:first-child img")}else{var g=e.next().children()}b.empty();g.clone().appendTo(b)}}function fullScreenFunc(){var a=document.getElementById("full_image");if(a.requestFullscreen){a.requestFullscreen()}else if(a.mozRequestFullScreen){a.mozRequestFullScreen()}else if(a.webkitRequestFullScreen){a.webkitRequestFullScreen()}}$(document).keydown(function(a){if(a.keyCode==37){var b="left";cycle(b);return false}if(a.keyCode==39){var b="right";cycle(b);return false}})